const allAnnouncements = [];

for (const item of $input.all()) {
  const htmlArray = item.json.html_content;

  if (Array.isArray(htmlArray)) {
    htmlArray.forEach(html => {
      const parsed = parseHTML(html);
      if (parsed && isTargetCategory(parsed.category) && isNewAnnouncement(parsed.date)) {
        allAnnouncements.push(parsed);
      }
    });
  } else if (typeof htmlArray === 'string') {
    const parsed = parseHTML(htmlArray);
    if (parsed && isTargetCategory(parsed.category) && isNewAnnouncement(parsed.date)) {
      allAnnouncements.push(parsed);
    }
  }
}

function parseHTML(html) {
  try {
    // åªæŠ“å‡ºã€Œä¸€èˆ¬ã€æˆ–ã€Œé‡è¦ã€é¡åˆ¥çš„åˆ†é¡
    const categoryMatch = html.match(/<span[^>]*class="badge[^"]*(bg-primary|bg-danger|bg-info|bg-success)[^"]*"[^>]*>([^<]+)<\/span>/);

    // æå–é€£çµå’Œæ¨™é¡Œ
    const linkMatch = html.match(/<a[^>]*href="([^"]*)"[^>]*>([^<]+)<\/a>/);

    // æå–æ—¥æœŸ
    const dateMatch = html.match(/<small[^>]*>(\d{4}\.\d{2}\.\d{2})<\/small>/);

    if (!linkMatch || !dateMatch) return null;

    let cleanLink = linkMatch[1].trim();

    // è£œé½Šç›¸å°è·¯å¾‘
    if (!cleanLink.startsWith('http')) {
      cleanLink = cleanLink.replace(/^\/+/, '');
      if (!cleanLink.startsWith('zh-hant/')) {
        cleanLink = 'zh-hant/' + cleanLink.replace(/^zh-hant\//, '');
      }
    }

    return {
      category: categoryMatch ? categoryMatch[2].trim() : null, // "ä¸€èˆ¬" æˆ– "é‡è¦"
      title: linkMatch[2].trim(),
      link: cleanLink,
      date: dateMatch[1].trim(),
      id: extractIdFromLink(cleanLink)
    };
  } catch (err) {
    console.log('è§£æå¤±æ•—:', err);
    return null;
  }
}

function extractIdFromLink(link) {
  const match = link.match(/\/(\d+)$/);
  return match ? match[1] : null;
}

// åƒ…ä¿ç•™ã€Œä¸€èˆ¬ã€æˆ–ã€Œé‡è¦ã€
function isTargetCategory(category) {
  return category === 'ä¸€èˆ¬' || category === 'é‡è¦' || category === 'ç ”ç©¶æ‰€' || category === 'ç¢©å£«';
}

function isNewAnnouncement(dateStr) {
  try {
    const parts = dateStr.match(/(\\d{4})\\.(\\d{2})\\.(\\d{2})/);
    if (!parts) return false;

    const announcementDate = new Date(parts[1], parts[2] - 1, parts[3]);
    const today = new Date();

    // é‡ç½®æ™‚é–“ç‚º 00:00:00 ä»¥ä¾¿ç²¾ç¢ºæ¯”è¼ƒæ—¥æœŸ
    announcementDate.setHours(0, 0, 0, 0);
    today.setHours(0, 0, 0, 0);

    // åªè¿”å›ä»Šå¤©çš„å…¬å‘Šï¼ˆç•¶æ—¥ 00:00-23:59ï¼‰
    return announcementDate.getTime() === today.getTime();
  } catch (e) {
    console.log('æ—¥æœŸè§£æå¤±æ•—:', e);
    return false;
  }
}

if (allAnnouncements.length === 0) {
  console.log('âš  æ²’æœ‰ç¬¦åˆæ¢ä»¶çš„å…¬å‘Š');
  return [];
}

console.log(`âœ… æ‰¾åˆ° ${allAnnouncements.length} å‰‡æ–°å…¬å‘Š`);
return [{ json: { announcements: allAnnouncements, count: allAnnouncements.length } }];

